mapper.select:
SELECT A.TYPE, A.NAME, A.CATEGORY FROM ( SELECT 'animal' AS TYPE, A.ANIMAL_NAME AS NAME, '병원진료' AS CATEGORY FROM ANIMAL_HOSPITAL_TREATMENT T JOIN ANIMAL A ON T.ANIMAL_ID = A.ANIMAL_ID WHERE T.DEL_YN = 'N' AND TO_CHAR(T.ANIMAL_VISIT_DATE, 'YYYY-MM-DD') = #{logDate}
UNION ALL
SELECT 'animal', A.ANIMAL_NAME, '훈련' FROM ANIMAL_TRAINING_ACTION T JOIN ANIMAL A ON T.ANIMAL_ID = A.ANIMAL_ID WHERE T.DEL_YN = 'N' AND TO_CHAR(T.ANIMAL_RECORD_DATE, 'YYYY-MM-DD') = #{logDate}
UNION ALL
SELECT 'animal', A.ANIMAL_NAME, '산책' FROM ANIMAL_WALK T JOIN ANIMAL A ON T.ANIMAL_ID = A.ANIMAL_ID WHERE T.DEL_YN = 'N' AND TO_CHAR(T.ANIMAL_RECORDED_DT, 'YYYY-MM-DD') = #{logDate}
UNION ALL
SELECT 'plant', P.PLANT_NAME, '물주기' FROM PLANT_WATERING T JOIN PLANT P ON T.PLANT_ID = P.PLANT_ID WHERE T.DEL_YN = 'N' AND TO_CHAR(T.PLANT_WATERING_DATE, 'YYYY-MM-DD') = #{logDate}
UNION ALL
SELECT 'plant', P.PLANT_NAME, '병충해' FROM PLANT_PEST T JOIN PLANT P ON T.PLANT_ID = P.PLANT_ID WHERE T.DEL_YN = 'N' AND TO_CHAR(T.PLANT_PEST_DATE, 'YYYY-MM-DD') = #{logDate}
UNION ALL
SELECT 'plant', P.PLANT_NAME, '분갈이' FROM PLANT_REPOTTING T JOIN PLANT P ON T.PLANT_ID = P.PLANT_ID WHERE T.DEL_YN = 'N' AND TO_CHAR(T.PLANT_REPOTTING_DATE, 'YYYY-MM-DD') = #{logDate}
UNION ALL
SELECT 'plant', P.PLANT_NAME, '일조량' FROM PLANT_SUNLIGHTING T JOIN PLANT P ON T.PLANT_ID = P.PLANT_ID WHERE T.DEL_YN = 'N' AND TO_CHAR(T.CREATE_DT, 'YYYY-MM-DD') = #{logDate} ) A ORDER BY A.TYPE, A.NAME
